import { useContext, useEffect, useRef, useState } from "react";
import { UserContext } from "../../Context/Authcontext";
import { updateProfile } from "firebase/auth";
import { doc, Firestore, setDoc } from "firebase/firestore";
import { _Auth, _DB } from "../../../Backend/Firebase";
import toast from "react-hot-toast";
const moods = [
  "Love",
  "Romantic",
  "Sad",
  "Happy",
  "Party",
  "Dance",
  "Chill",
  "Energetic",
  "Motivational",
  "Melody",
  "Relax",
  "Workout",
  "Festival",
];

const musicDirectors = [
  {
    name: "A. R. Rahman",
    img: "https://c.ndtvimg.com/2025-09/onnvgrbk_rahman-_625x300_02_September_25.jpeg?im=FeatureCrop,algorithm=dnn,width=773,height=435",
  },
  {
    name: "Pritam",
    img: "https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcS4Kyw_9NzLk5TQfBoW8kA8VaGX5WD4SKOFJCfhehQxgLMDKNBT4tx21-MB0dgu6TgE_K8qCKGtXQ72oEVhnM2lFrvip9KlwII1yju89eNF",
  },
  {
    name: "Ilaiyaraaja",
    img: "data:image/jpeg;base64,",
  },
  {
    name: "Anirudh",
    img: "https://upload.wikimedia.org/wikipedia/commons/d/d4/Anirudh_Ravichander_at_Audi_Ritz_Style_Awards_2017_%28cropped%29.jpg",
  },
  {
    name: "Shankar–Ehsaan–Loy",
    img: "data:image/jpeg;base64,",
  },
  {
    name: "Vishal–Shekhar",
    img: "data:image/jpeg;base64,",
  },
  {
    name: "Harris Jayaraj",
    img: "data:image/jpeg;base64,",
  },
  {
    name: "Deva",
    img: "data:image/jpeg;base64,",
  },
  {
    name: "M. M. Keeravani",
    img: "data:image/jpeg;base64,",
  },
  {
    name: "Yuvan Shankar Raja",
    img: "data:image/jpeg;base64,",
  },
];

const Addprofile = () => {
  let userDetails = useContext(UserContext);
  let [photo, setPhoto] = useState(null);
  let [data, setData] = useState({
    firstname: "",
    lastname: "",
    dob: "",
    gender: "",
    age: "",
    language: "",
    phoneno: "",
    usertype: "user",
  });
  const [selectedMoods, setSelectedMoods] = useState([]);
  const [selectedDirectors, setSelectedDirectors] = useState([]);
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleMood = (mood) => {
    setSelectedMoods((prev) =>
      prev.includes(mood) ? prev.filter((s) => s !== mood) : [...prev, mood]
    );
    console.log(setSelectedMoods);
    setOpen(false);
  };

  const removeMood = (mood) => {
    setSelectedMoods((prev) => prev.filter((s) => s !== mood));
  };

  const toggleDirector = (name) => {
    setSelectedDirectors((prev) =>
      prev.includes(name) ? prev.filter((d) => d !== name) : [...prev, name]
    );
  };

  const removeDirector = (name) => {
    setSelectedDirectors((prev) => prev.filter((d) => d !== name));
  };

  function handleChange(e) {
    if (e.target.id == "photo") {
      setPhoto(e.target.files[0]);
    } else if (e.target.id === "dob") {
      const dob = e.target.value;
      const today = new Date();
      const birthDate = new Date(dob);

      let age = today.getFullYear() - birthDate.getFullYear();
      const m = today.getMonth() - birthDate.getMonth();

      if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
        age--;
      }

      setData({
        ...data,
        dob: dob,
        age: age.toString(),
      });
    } else {
      setData({
        ...data,
        [e.target.id]: e.target.value,
      });
    }
  }

  async function handleSubmit(e) {
    e.preventDefault();
    const toastermessage = toast.loading("Adding profile, please wait");
    if (photo) {
      const formData = new FormData();
      formData.append("file", photo);
      formData.append("upload_preset", "BAX_APPLICATIONS");
      const urlResponse = await fetch(
        "https://api.cloudinary.com/v1_1/dvzeqswan/upload",
        {
          method: "POST",
          body: formData,
        }
      );
      const data = await urlResponse.json();
      await updateProfile(userDetails, {
        photoURL: data.secure_url,
      });
    }
    try {
      if (!userDetails || !userDetails.uid) {
        console.error("User UID not found");
        return;
      }

      data = { ...data, music_genre: selectedMoods };
      data = { ...data, music_directors: selectedDirectors };
      await setDoc(doc(_DB, "users", userDetails.uid), data);
      toast.dismiss(toastermessage);
      toast.success("Profile successfully Added");
      console.log("User data saved successfully!");
      window.location.assign("/");
    } catch (error) {
      console.error("Firestore error:", error);
    }
  }
  return (
    <>
      {" "}
      <section className=" w-full h-[calc(100vh-18px)] static bg-red-500 hidden lg:block bg-[url(https://media0.giphy.com/media/v1.Y2lkPTc5MGI3NjExenJ1Y3huZnZlNWJqaWE2dGhsa3JzNnk5MTR2bDRna3Nxc3J2eHBvOCZlcD12MV9naWZzX3NlYXJjaCZjdD1n/Sl7OlpTiHi9pPPZKp4/giphy.webp)] bg-no-repeat bg-cover bg-center"></section>
      <form
        className="min-h-screen w-full flex items-center justify-center bg-gradient-to-br from-gray-100 to-gray-300 p-6"
        onSubmit={handleSubmit}
      >
        <section className="w-full max-w-2xl bg-white shadow-2xl rounded-lg p-8">
          <header className="text-3xl font-extrabold text-center text-gray-800 mb-8">
            Add Your Details
          </header>

          <div className="flex flex-col gap-5">
            <div className="flex gap-4">
              <div>
                <label
                  htmlFor="firstname"
                  className="font-semibold mb-1 text-gray-700"
                >
                  First Name:
                </label>
                <input
                  type="firstname"
                  id="firstname"
                  value={data.firstname}
                  onChange={handleChange}
                  placeholder="Enter your first name"
                  className="rounded-md border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-pink-400 focus:outline-none"
                />
              </div>
              <div>
                <label
                  htmlFor="lastname"
                  className="font-semibold mb-1 text-gray-700"
                >
                  Last Name:
                </label>
                <input
                  type="lastname"
                  id="lastname"
                  value={data.lastname}
                  onChange={handleChange}
                  placeholder="Enter your last name"
                  className="rounded-md border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-pink-400 focus:outline-none"
                />
              </div>
            </div>
            <div className=" flex gap-4 w-full justify-center items-center">
              <label htmlFor="photo">UploadPhoto:</label>
              <input
                id="photo"
                type="file"
                accept="image/*"
                onChange={handleChange}
                className="file:border-black  file:rounded-md file:px-3 file:py-2 file:bg-slate-500 focus:outline-0 placeholder-black w-full border-1 border-black rounded-md text-black p-2"
                name="photo"
              />
            </div>

            <div className="flex flex-col">
              <label htmlFor="dob" className="font-semibold mb-1 text-gray-700">
                Date of Birth
              </label>
              <input
                type="date"
                id="dob"
                value={data.dob}
                onChange={handleChange}
                className="rounded-md border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-pink-400 focus:outline-none"
              />
            </div>

            <div className="flex flex-col">
              <label
                htmlFor="phoneno"
                className="font-semibold mb-1 text-gray-700"
              >
                Phone Number
              </label>
              <input
                type="tel"
                id="phoneno"
                value={data.phoneno}
                onChange={handleChange}
                placeholder="Enter your number"
                className="rounded-md border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-pink-400 focus:outline-none"
              />
            </div>

            <div className="flex flex-col">
              <label
                htmlFor="gender"
                className="font-semibold mb-1 text-gray-700"
              >
                Gender
              </label>
              <select
                name="gen"
                id="gender"
                value={data.gender}
                onChange={handleChange}
                className="rounded-md border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-pink-400 focus:outline-none"
              >
                <option value="" disabled selected>
                  -- Select Gender --
                </option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
            </div>
            <div className="flex flex-col">
              <label
                htmlFor="language"
                className="font-semibold mb-1 text-gray-700"
              >
                Preferred Language
              </label>
              <select
                name="lang"
                value={data.language}
                id="language"
                onChange={handleChange}
                className="rounded-md border border-gray-300 px-3 py-2 focus:ring-2 focus:ring-pink-400 focus:outline-none"
              >
                <option value="" disabled selected>
                  -- Select Language --
                </option>
                <option value="Hindi">Hindi (हिन्दी)</option>
                <option value="Tamil">Tamil (தமிழ்)</option>
                <option value="Telugu">Telugu (తెలుగు)</option>
                <option value="Malayalam">Malayalam (മലയാളം)</option>
                <option value="Kannada">Kannada (ಕನ್ನಡ)</option>
              </select>
            </div>
            <div className="flex flex-col mb-6 relative" ref={dropdownRef}>
              <label className="font-semibold mb-2 text-gray-700">
                Music Mood
              </label>

              <div
                className="flex flex-wrap gap-2 p-2 border rounded-lg cursor-pointer focus:ring-2 focus:ring-pink-400"
                onChange={handleChange}
                onClick={() => setOpen(!open)}
              >
                {selectedMoods.length === 0 && (
                  <span className="text-gray-400">-- Select Mood(s) --</span>
                )}
                {selectedMoods.map((mood) => (
                  <span
                    key={mood}
                    className="bg-pink-100 text-pink-700 px-3 py-1 rounded-full text-sm flex items-center gap-2"
                  >
                    {mood}
                    <button
                      type="button"
                      onClick={(e) => {
                        e.stopPropagation();
                        removeMood(mood);
                      }}
                      className="text-pink-600 hover:text-pink-800"
                    >
                      ✕
                    </button>
                  </span>
                ))}
              </div>

              {open && (
                <div className="mt-2 border rounded-lg shadow-md bg-white max-h-48 overflow-y-auto absolute z-10 w-full">
                  {moods.map((mood) => (
                    <div
                      key={mood}
                      onClick={() => toggleMood(mood)}
                      onChange={handleChange}
                      className={`px-3 py-2 cursor-pointer flex justify-between items-center hover:bg-pink-50 ${
                        selectedMoods.includes(mood)
                          ? "bg-pink-100 font-semibold"
                          : ""
                      }`}
                    >
                      <span>{mood}</span>
                      {selectedMoods.includes(mood) && (
                        <span className="text-green-600 font-bold">✔</span>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="mt-10 p-6 bg-white shadow-lg rounded-lg">
              <h2 className="text-2xl font-bold mb-6 text-center">
                🎶 Select Your Favorite Music Directors
              </h2>
              <div className="flex flex-wrap gap-2 mb-4">
                {selectedDirectors.length === 0 && (
                  <span className="text-gray-400"></span>
                )}
                {selectedDirectors.map((dir) => (
                  <span
                    key={dir}
                    className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm flex items-center gap-2"
                  >
                    {dir}
                    <button
                      type="button"
                      onClick={() => removeDirector(dir)}
                      className="text-blue-600 hover:text-blue-800"
                    >
                      ✕
                    </button>
                  </span>
                ))}
              </div>

              <div className="overflow-x-auto border rounded-lg p-4 bg-gray-50">
                <div className="flex flex-nowrap space-x-6">
                  {musicDirectors.map((dir, index) => {
                    const isSelected = selectedDirectors.includes(dir.name);
                    return (
                      <div
                        key={index}
                        onClick={() => toggleDirector(dir.name)}
                        className={`flex-shrink-0 w-40 flex flex-col items-center p-4 rounded-lg shadow cursor-pointer transition ${
                          isSelected
                            ? "bg-pink-100 border-2 border-pink-500"
                            : "bg-white hover:shadow-md"
                        }`}
                      >
                        <img
                          src={dir.img}
                          alt={dir.name}
                          className={`w-20 h-20 rounded-full object-cover border-2 ${
                            isSelected ? "border-pink-500" : "border-gray-300"
                          }`}
                        />
                        <p className="mt-3 font-semibold text-gray-800 text-center">
                          {dir.name}
                        </p>
                        {isSelected && (
                          <span className="text-green-600 mt-2 font-bold">
                            ✔ Selected
                          </span>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
            <button
              type="submit"
              className="w-full mt-6 bg-gradient-to-r from-pink-500 to-red-500 text-white font-semibold py-3 rounded-md shadow-lg hover:opacity-90 transition"
            >
              Save Details
            </button>
          </div>
        </section>
      </form>
    </>
  );
};

export default Addprofile;
